import { Fragment } from 'react';
import { useHistory, useLocation } from 'react-router-dom';

import QuoteItem from './QuoteItem';
import classes from './QuoteList.module.css';

const QuoteList = (props) => {

  const history = useHistory();

  //object that has info about current page
  const location = useLocation();



  //built in constructor function that can extract data from the location object.
  const queryParams = new URLSearchParams(location.search);

  console.log(location.search)

  const isSortingAscending = queryParams.get('sort') === "ascend";


const changeSortingHandler = () => {
history.push('allQuotes?sort=' + "ascend")
}

  return (
    <Fragment>

<div className={classes.sorting}>
<button onClick={changeSortingHandler} >Sort Ascending</button>

</div>


      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
